RESOURCE_TABLES.INIT_TABLE_DOC_mysql:
CREATE TABLE SAMPLE_TABLE ( REC_ID bigint NOT NULL , REC_DOCID bigint NULL , REC_RESID bigint NULL , REC_SH varchar(1) NULL , REC_SHNAME varchar(50) NULL , REC_SHTIME datetime NULL , REC_ON varchar(1) NULL , REC_CLOSE varchar(1) NULL , REC_OFF varchar (1) NULL , REC_DATE datetime NULL , REC_YEAR int NULL , REC_QUARTER int NULL , REC_MONTH int NULL , REC_WEEK int NULL , REC_YM int NULL , REC_DEP1 varchar(15) NULL , REC_DEP2 varchar(15) NULL , REC_DEP3 varchar(15) NULL , REC_DEP4 varchar(15) NULL )
;
ALTER TABLE SAMPLE_TABLE add CONSTRAINT PK_SAMPLE_TABLE PRIMARY KEY ( REC_ID ) ;
CREATE INDEX IX_SAMPLE_TABLE ON SAMPLE_TABLE(REC_RESID) ;

